import React from 'react'
import { MapContainer, LayersControl, TileLayer, Marker, Popup, GeoJSON } from 'react-leaflet';
import { Drawer, Button, Radio, Space } from 'antd';
import * as polygonData from '../data/features.json';

export default function Map() {

    state = { visible: false, placement: 'left' };

    showDrawer = () => {
        this.setState({
        visible: true,
        });
    };

    onClose = () => {
        this.setState({
        visible: false,
        });
    };

    var myStyle = {
        "opacity": 0.1,
    };

    function whenClicked(e) {
        // console.log(e);
        this.showDrawer
      }

    //function to show popup when hover
    const onEachSub = (feature, layer) =>{
        const subName = feature.properties.vic_lga__2;   
        layer.on('mouseover', function (e) {
            layer.bindPopup(subName).openPopup(); // here add openPopup()
            layer.setStyle({
                fillOpacity: 0.6
            });
        });

        layer.on('mouseout', function (e) {
            layer.setStyle({
                fillOpacity: 0.1
            });
        });

        layer.on({
            click: whenClicked
        });
    }


    return (
        <>
            <MapContainer center={[-37.813629, 144.963058]} zoom={18} scrollWheelZoom={true} style={{ height: "100vh" }}>
                <LayersControl position="topright">
                    {/* The style of map */}
                    <TileLayer
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a > contributors'
                    />

                    {/* Layer of marker */}
                    <LayersControl.Overlay name="Marker with popup">
                        <Marker position={[-37.813629, 144.963058]} iconUrl={"https://static.thenounproject.com/png/780108-200.png"} >
                            <Popup>Melbourne city</Popup>
                        </Marker>
                    </LayersControl.Overlay>

                    {/* Layer of hotmap */}
                    <LayersControl.Overlay name="Feature group">
                    <GeoJSON 
                        data={polygonData.features}
                        style={myStyle}
                        onEachFeature={onEachSub}
                    />
                    </LayersControl.Overlay>

                </LayersControl>
            </MapContainer>
            <Drawer
                title="Basic Drawer"
                placement={placement}
                closable={false}
                onClose={this.onClose}
                visible={visible}
                key={placement}
                >
                <p>Some data...</p>
                <p>Some data...</p>
                <p>Some condatatents...</p>
            </Drawer>
     </>
    )
}
